<?phpclass request_library extends app_library {	private function _remove_magic_quotes($value){		if(get_magic_quotes_gpc()){			if(is_array($value)){				foreach($value as $key => $value_value){					$value[$key] = stripslashes($value_value);				}			} else {				$value = stripslashes($value);			}		}					return $value;	}	private function _fetch($array, $key, $default){		if($key === FALSE){			return (count($array) > 0);		}		if(isset($array[$key])){			return $this->_remove_magic_quotes($array[$key]);		}		return $default;	}	public function get($key = FALSE, $default = FALSE){		return $this->_fetch($_GET, $key, $default);	}	public function post($key = FALSE, $default = FALSE){		return $this->_fetch($_POST, $key, $default);	}	public function cookie($key = FALSE, $default = FALSE){		return $this->_fetch($_COOKIE, $key, $default);	}	public function header($key = FALSE, $default = FALSE){		if(function_exists("getallheaders")){			$headers = getallheaders();		} else {			$headers = $_SERVER;			$key = "HTTP_".(str_replace("-", "_", strtoupper($key)));		}		return $this->_fetch($headers, $key, $default);	}	public function set_cookie(){		return call_user_func_array("setcookie", func_get_args());	}	public function delete_cookie($key){		return $this->set_cookie($key, "", 1);	}}